mapper.select:
select
id, title, abstract_txt, content, author, author_id, enclosure, create_time, update_time, delete_status
from blog.article


        and id = #{id}


        and title = #{title}


        and abstract_txt = #{abstractTxt}


        and content = #{content}


        and author = #{author}


        and author_id = #{authorId}


        and enclosure = #{enclosure}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and delete_status = #{deleteStatus}

mapper.update:
UPDATE article c1,article c2
SET c1.top_status = '1', c2.`top_status`='0'
WHERE c2.`top_status` = '1' AND c1.`id` = #{id}
